sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	alert("Full Name:"+g_user.getFullName());
	if(g_user.hasRoles())
			alert("User has some Roles.");
	else
			alert("User doesn't have Role");
   if(g_user.hasRoleExactly("developer"))
		   alert("User has developer role.");
	else
		alert("User doesnt have developer role.");
	if(g_user.hasRole("developer"))
		alert("User has Developer or admin role");
	else
		alert("User doesnot have developer or admin role");
	if(g_user.hasRoleFromList("developer","finance"))
		alert("user has any one role from the list or admin role");
	else
		alert("user doesnt have  any role from list or admin role");
}
